mapper.select:
SELECT USERID, PASSWORD, NAME, SSN, AGE, GENDER, TEAMID, ROLL, PHONE, EMAIL
FROM MEMBER

	
		USERID LIKE #{userid}
	
	
		AND PASSWORD LIKE #{password}

mapper.update:
UPDATE MEMBER SET
  password = #{password},
  teamid = #{teamid},
  roll = #{roll}
WHERE userid = #{userid}
